import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
})
export class HomeComponent {
}
<div class="text-center p-8">
  <h1 class="text-5xl font-bold mb-4 main-text">Переводим расписание на новый уровень!</h1>
  <p class="text-xl mb-8 text">
    С нашем приложением, деканаты с легкостью снерируют оптимизированные расписания,<br>
    обеспечивая эффективное использование ресурсов и гибкость в управлении.
  </p>
  <img src="assets/images/home/Board1.png" alt="Hero image with two individuals using a laptop" class="mx-auto">
</div>
